Method.Implementation:

	s html = ..Html 
	d html.Rewind()
	s ..pos=0, ..row = 0 

	s pos = html.FindAt( 1, ..#TopicTableBegin ) 
    if pos = -1 {
	    s sc = $$$ERROR(,"Not found begin of topic table") Q 0
    }
    s pos = pos + $l(..#TopicTableBegin)

    s end = html.FindAt( pos, ..#TopicTableEnd ) 
    if end = -1 {
	    s sc = $$$ERROR(,"Not found end of topics table") Q 0
    }

	s sc = html.MoveTo( pos ) Q:'sc ""

	while pos < end {

		s pos = html.FindAt( pos, ..#TopicBegin ) Q:pos=-1
		s sc = ..topicBegin( pos ) Q:'sc 
		s pos = pos + $l(..#TopicBegin) ;offset

	}
	if sc && ( ..pos < end) {
		s sc = ..topicBegin( end ) ;for last topic	
	}
	Q sc

s date = $p( dtexpr,","), date = $zstrip(date, "<>WC" ) ;30 июн 15 s time = $p( dtexpr,",",2), time = $zstrip(time, "<>WC" ) ;13:46
s months = $zcvt(" Янв Фев Мар Апр Май Июн Июл Авг Сен Окт Ноя Дек", "L" )
///convert as two digits year from 2000-01-01 #define 2000 58074
if $f(date, "сегодня") s date = $zd( +$h, 2, months, 3, $$$2000) if $f(date, "вчера") s date = $zd( $h-1, 2, months, 3, $$$2000)
s dateh = $zdh( date, 2, months, 3, $$$2000 ) s timeh = $zth( time ) s ts = $zdt( dateh_","_timeh, 3 ) ;odbc timestamp Q ts
